#include <string>
#include <algorithm>
#include "__debug"
using namespace std;

class Solution1 {
public:

    bool isPalindrome(string s, int start, int end) {
        while (start < end) {
            if (s[start] != s[end]) {
                return false;
            }
            ++start;
            --end;
        }
        return true;
    }

    string shortestPalindrome(string s) {
        string prefix = "";
        int len = s.size();
        for (int i = len-1; i > 0; --i) {
            if (isPalindrome(s, 0, i)) {
                break;
            }
            prefix += s[i];
        }
        return prefix + s;
    }
};

class Solution {
public:

    void get_nextVec(string &s1, vector<int> &nextVec) {
        int i = 0;
        int j = -1;
        nextVec[i] = j;
        while (i < s1.size()-1) {
            if (j < 0 || s1[i] == s1[j]) {
                ++i;
                ++j;
                nextVec[i] = j;
            } else {
                j = nextVec[j];
            }
        }
    }

    string shortestPalindrome(string s) {
        if (s.size() <= 1) {
            return s;
        }
        string s1 = s;
        string s2 = s;
        reverse(s1.begin(), s1.end());
        s2 = s + "#" + s1;
        vector<int> nextVec(s2.size(), 0);
        get_nextVec(s2, nextVec);
//        _print(nextVec);
        return s1.substr(0, s1.size() - nextVec[s2.size()-1] - 1) + s;
    }
};

void func() {
    Solution solution;
    Solution1 solution1;
    string str1 = "aa";
    string str2 = "aacecaaa";
    string str3 = "";
    cout << solution.shortestPalindrome(str1) << endl;
    cout << solution.shortestPalindrome(str2) << endl;
    string ss = solution.shortestPalindrome(str3);
    string ss1 = solution1.shortestPalindrome(str3);
    cout << (ss == ss1) << endl;
    cout << ss.size() << endl;
    cout << ss1.size() << endl;
    cout << str3.size() << endl;
}

