#ifndef ONLINEJUDGE__C214_H
#define ONLINEJUDGE__C214_H
#include <string>
using namespace std;

class Solution {
public:

    bool isPalindrome(string s, int start, int end) {
        while (start < end) {
            if (s[start] != s[end]) {
                return false;
            }
            ++start;
            --end;
        }
        return true;
    }

    string shortestPalindrome(string s) {
        string prefix = "";
        int len = s.size();
        for (int i = len-1; i > 0; --i) {
            if (isPalindrome(s, 0, i)) {
                break;
            }
            prefix += s[i];
        }
        return prefix + s;
    }
};

void func() {
    Solution solution;
    string str1 = "abcd";
    string str2 = "aacecaaa";
    string str3 = "";
    cout << solution.shortestPalindrome(str1) << endl;
    cout << solution.shortestPalindrome(str2) << endl;
    string ss = solution.shortestPalindrome(str3);
    cout << ss.size() << endl;
    cout << str3.size() << endl;
}

#endif //ONLINEJUDGE__C214_H
